import "core-js/modules/es.array.push.js";
import { GoodWizard } from 'vue-good-wizard';
import { validationMixin } from 'vuelidate';
import { required, minValue, alpha, minLength, numeric, decimal } from 'vuelidate/lib/validators';
import { createLot } from '@/services/LotService';
import { getAllProducts } from '@/services/ProductService';
import { getAllTrademarks } from '@/services/TrademarkService';
export default {
  name: 'CreateLodgementWizard',
  mixins: [validationMixin],
  components: {
    'vue-good-wizard': GoodWizard
  },
  data: function () {
    return {
      steps: [{
        label: 'Details',
        slot: 'page1'
      }, {
        label: 'Charges',
        slot: 'page2'
      }, {
        label: 'Confirm',
        slot: 'page3',
        options: {
          nextDisabled: true
        }
      }],
      trademarkOptions: [],
      productOptions: [],
      selectedTrademark: {
        _id: "",
        trademarkName: "",
        customerName: "",
        mobileNumber: "",
        town: ""
      },
      selectedProduct: {
        _id: "",
        prouctName: "",
        productSubGrouPrefix: "",
        hamaliPerBag: 0,
        mamulluPerBag: 0,
        insurancePerBag: 0,
        kataCooliePerBag: 0,
        platformCooliePerBag: 0
      },
      detailsForm: {
        _id: null,
        lotNumber: null,
        trademark: null,
        product: null,
        numBagsLodged: null,
        numBagsKataCoolie: null,
        locations: null
        // vehicleNumber: null,
        // driverName: null
      },

      chargesForm: {
        hamaliCharges: 0,
        mamulluCharges: 0,
        platformCoolieCharges: 0,
        kataCoolieCharges: 0,
        insuranceCharges: 0,
        totalChargesReceivable: 0,
        hamaliChargesReceived: null,
        mamulluChargesReceived: null,
        transportChargesReceivable: null,
        monthlyRent: 0,
        yearlyRent: 0
      }
    };
  },
  methods: {
    nextClicked(currentPage) {
      console.log(currentPage);
      if (currentPage == 0) {
        console.log(this.detailsForm);
        this.$v.detailsForm.$touch();
        if (this.$v.detailsForm.$anyError) {
          console.log("There are errors in the details form");
          return false;
        }
      } else if (currentPage == 1) {
        this.$v.chargesForm.$touch();
        if (this.$v.chargesForm.$anyError) {
          console.log("There are errors in the charges form");
          return false;
        }
      } else if (currentPage == 2) {
        console.log("submitting form");
        this.handleSubmit();
        return true;
      }
      return true;
    },
    backClicked(currentPage) {
      return true;
    },
    prepareProductOption(item, index) {
      this.productOptions.push({
        "value": item,
        "text": item.productName
      });
    },
    prepareTrademarkOption(item, index) {
      this.trademarkOptions.push({
        "value": item,
        "text": item.trademarkName + " ( " + item.customerName + " )"
      });
    },
    updateSelectedProduct(option) {
      console.log("Updated Product : ", option);
      this.selectedProduct = option;
    },
    updateSelectedTrademark(option) {
      console.log("Updated Trademark : ", option);
      this.selectedTrademark = option;
    },
    handleSubmit() {
      // Add a new Lodgement
      createNewLodgement();

      // Hide the modal manually
      this.$nextTick(() => {
        // redirect to lot details page
      });
    },
    createNewLodgement() {
      let creationDate = new Date();
      let newLot = {
        lodgementDate: creationDate,
        lotNumber: this.detailsForm.lotNumber,
        numBagsLodged: this.detailsForm.numBagsLodged,
        numBagsBalance: this.detailsForm.numBagsLodged,
        numBagsKataCoolie: this.detailsForm.numBagsKataCoolie,
        numDeliveries: 0,
        locationCodes: this.detailsForm.locations,
        lastTransactionDate: creationDate,
        trademark: {
          trademarkId: this.selectedTrademark._id,
          trademark: this.selectedTrademark.trademarkName,
          customerName: this.selectedTrademark.customerName
        },
        product: {
          productId: this.selectedProduct._id,
          productName: this.selectedProduct.productName
        },
        charges: {
          hamaliCharges: this.chargesForm.hamaliCharges,
          platformCoolieCharges: this.chargesForm.platformCoolieCharges,
          kataCoolieCharges: this.chargesForm.kataCoolieCharges,
          mamulluCharges: this.chargesForm.mamulluCharges,
          insuranceCharges: this.chargesForm.insuranceCharges,
          totalLodgementChargesReceivable: this.chargesForm.totalChargesReceivable,
          totalLodgementChargesReceived: this.chargesForm.hamaliChargesReceived,
          totalDeliveryChargesReceivable: 0,
          totalDeliveryChargesReceived: 0
        },
        vehicleNumber: this.detailsForm.vehicleNumber,
        driverName: this.detailsForm.driverName,
        transportChargesPaid: this.chargesForm.transportChargesReceivable,
        rents: {
          rentalYear: this.getRentalYear,
          rentalMode: this.getRentalMode,
          totalRentReceivable: this.chargesForm.monthlyRent,
          totalRentReceived: this.chargesForm.yearlyRent
        },
        deliveries: []
      };
      createLot(newLot).then(response => {
        const newLotId = response;
        let createdLotUrl = '/inventory/lots/' + newLotId;
        console.log("createdLotID: ", newLotId);
        console.log("createdLotUrl: ", createdLotUrl);
        this.$router.push(createdLotUrl);
      });
    }
  },
  mounted() {
    getAllProducts().then(response => {
      const items = response;
      items.forEach(this.prepareProductOption);
    });
    getAllTrademarks().then(response => {
      const items = response;
      items.forEach(this.prepareTrademarkOption);
    });
  },
  computed: {
    getSelectedTrademark() {
      return this.selectedTrademark != null ? this.selectedTrademark : {
        _id: "",
        trademarkName: "",
        customerName: "",
        town: "",
        mobileNumber: ""
      };
    },
    getSelectedProduct() {
      return this.selectedProduct != null ? this.selectedProduct : {
        productId: null,
        productName: "",
        productSubGrouPrefix: "",
        bagSize: 0
      };
    },
    getNumBagsLodged() {
      return this.detailsForm.numBagsLodged;
    },
    getNumBagsKataCoolie() {
      return this.detailsForm.numBagsKataCoolie;
    },
    getHamaliChargesReceivable() {
      let charges = Math.round(this.selectedProduct.hamaliPerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.hamaliCharges = charges;
      return charges + " ( Rs. " + this.selectedProduct.hamaliPerBag + "/- per bag)";
    },
    getMamulluChargesReceivable() {
      let charges = Math.round(this.selectedProduct.mamulluPerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.mamulluCharges = charges;
      return charges + " ( Rs. " + this.selectedProduct.mamulluPerBag + "/- per bag)";
    },
    getPlatformCoolieChargesReceivable() {
      let charges = Math.round(this.selectedProduct.platformCooliePerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.platformCoolieCharges = charges;
      return charges + " ( Rs. " + this.selectedProduct.platformCooliePerBag + "/- per bag)";
    },
    getKataCoolieChargesReceivable() {
      let charges = Math.round(this.selectedProduct.kataCooliePerBag * this.detailsForm.numBagsKataCoolie);
      this.chargesForm.kataCoolieCharges = charges;
      return charges + " ( Rs. " + this.selectedProduct.kataCooliePerBag + "/- per bag)";
    },
    getInsuranceChargesReceivable() {
      let charges = Math.round(this.selectedProduct.insurancePerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.insuranceCharges = charges;
      return charges + " ( Rs. " + this.selectedProduct.insurancePerBag + "/- per bag)";
    },
    getTotalChargesReceivable() {
      let totalCharges = Math.round(this.chargesForm.hamaliCharges + this.chargesForm.mamulluCharges + this.chargesForm.kataCoolieCharges + this.chargesForm.platformCoolieCharges + this.chargesForm.insuranceCharges);
      return totalCharges;
    },
    getRentalMode() {
      let today = new Date();
      return today.getMonth() < 5 ? "Yearly" : "Monthly";
    },
    getRentalYear() {
      let today = new Date();
      return today.getFullYear();
    },
    getMonthlyRent() {
      let charges = Math.round(this.selectedProduct.monthlyRentPerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.monthlyRent = charges;
      return this.getRentalMode != "Monthly" ? 0 : charges + " ( Rs. " + this.selectedProduct.monthlyRentPerBag + "/- per bag)";
    },
    getYearlyRent() {
      let charges = Math.round(this.selectedProduct.yearlyRentPerBag * this.detailsForm.numBagsLodged);
      this.chargesForm.yearlyRent = charges;
      return this.getRentalMode != "Yearly" ? 0 : charges + " ( Rs. " + this.selectedProduct.yearlyRentPerBag + "/- per bag)";
    }
  },
  validations: {
    detailsForm: {
      lotNumber: {
        required,
        minLength: minLength(6)
      },
      trademark: {
        required
      },
      product: {
        required
      },
      numBagsLodged: {
        required,
        numeric,
        minValueValue: minValue(0)
      },
      numBagsKataCoolie: {
        required,
        numeric,
        minValueValue: minValue(0)
        // maxValueRef: maxValue(this.detailsForm.numBagsLodged)
      },

      locations: {
        required,
        minLength: 5
      }
      // driverName: {
      //     alpha
      // },
      // vehicleNumber: {

      // }
    },

    chargesForm: {
      hamaliChargesReceived: {
        required,
        decimal,
        minValueValue: minValue(0)
      },
      mamulluChargesReceived: {
        required,
        decimal,
        minValueValue: minValue(0)
      },
      transportChargesReceivable: {
        required,
        decimal,
        minValueValue: minValue(0)
      }
    }
  }
};
