import "core-js/modules/es.array.push.js";
import ProductFormSchema from './schemas/ProductFormSchema';
import { getAllProductGroups } from '@/services/ProductGroupService';
import { createProduct, updateProduct } from '@/services/ProductService';
import Multiselect from 'vue-multiselect';
export default {
  name: 'ProductForm',
  components: {
    Multiselect
  },
  props: ["formData", "submitMode"],
  data: function () {
    return {
      formSchema: ProductFormSchema,
      formOptions: {
        validateAfterChanged: true
      },
      loadedProductId: "",
      isSaving: false,
      formModel: {
        productName: "",
        productSubGroupPrefix: "",
        bagSize: 0,
        computedProductName: "",
        hamaliPerBag: 0,
        platformCooliePerBag: 0,
        kataCooliePerBag: 0,
        mamulluPerBag: 0,
        insurancePerBag: 0,
        monthlyRentPerBag: 0,
        yearlyRentPerBag: 0,
        yearlyRentPerKg: 0,
        computedYearlyRentPerBag: 0
      },
      productGroupOptions: []
    };
  },
  created() {
    this.formModel.productName = this.formData.productName;
    this.formModel.productSubGroupPrefix = this.formData.productSubGroupPrefix;
    this.formModel.computedProductName = this.formData.computedProductName;
    this.formModel.bagSize = this.formData.bagSize;
    this.formModel.hamaliPerBag = this.formData.hamaliPerBag;
    this.formModel.platformCooliePerBag = this.formData.platformCooliePerBag;
    this.formModel.kataCooliePerBag = this.formData.kataCooliePerBag;
    this.formModel.mamulluPerBag = this.formData.mamulluPerBag;
    this.formModel.insurancePerBag = this.formData.insurancePerBag;
    this.formModel.monthlyRentPerBag = this.formData.monthlyRentPerBag;
    this.formModel.yearlyRentPerKg = this.formData.yearlyRentPerKg;
    this.formModel.yearlyRentPerBag = this.formData.yearlyRentPerBag;
    this.formModel.computedYearlyRentPerBag = this.formData.computedYearlyRentPerBag;
    this.loadedProductId = this.formData._id;
    let productGroupField = this.formSchema.groups[0].fields.find(fields => fields.model === 'productSubGroupPrefix');
    productGroupField.values = this.getProductGroupOptions();
  },
  methods: {
    getProductGroupOptions() {
      getAllProductGroups().then(response => {
        let productGroups = response;
        productGroups.forEach(this.addToProductGroupOptions);
      });
      return this.productGroupOptions;
    },
    addToProductGroupOptions(productGroup) {
      this.productGroupOptions.push({
        "_id": productGroup._id,
        "name": productGroup.productSubGroupPrefix
      });
    },
    submitProductDetails() {
      let product = {
        productName: this.formModel.productName,
        productSubGroupPrefix: this.formModel.productSubGroupPrefix.name,
        bagSize: this.formModel.bagSize,
        computedProductName: this.formModel.computedProductName,
        hamaliPerBag: this.formModel.hamaliPerBag,
        platformCooliePerBag: this.formModel.platformCooliePerBag,
        kataCooliePerBag: this.formModel.kataCooliePerBag,
        mamulluPerBag: this.formModel.mamulluPerBag,
        insurancePerBag: this.formModel.insurancePerBag,
        monthlyRentPerBag: this.formModel.monthlyRentPerBag,
        yearlyRentPerKg: this.formModel.yearlyRentPerKg,
        computedYearlyRentPerBag: this.formModel.computedYearlyRentPerBag,
        yearlyRentPerBag: this.formModel.yearlyRentPerBag
      };
      if (this.submitMode == "update") {
        product._id = this.loadedProductId;
        updateProduct(this.loadedProductId, product).then(response => {
          var saveActionStatus = response;
          this.$emit('productSaved', saveActionStatus);
        });
      } else if (this.submitMode == "create") {
        product.creationDate = Date.now();
        console.log("Create Product : ", product);
        createProduct(product).then(response => {
          var createdProduct = response;
          this.$emit('productSaved', createdProduct);
        });
      }
    },
    cancelProductForm() {
      this.$emit('productEditCancelled');
    }
  }
};
