import { VueFormGenerator } from "vue-form-generator"

export default {
    groups: [
        {
            legend: "Product Group Details",
            fields: [
                {
                    type: 'input',
                    inputType: 'text',
                    label: 'Product Group',
                    model: 'productGroup',
                    required: true,
                    validator: ['string', 'required'],
                    onChanged: function(model, newVal, oldVal, field) {
                        model.productSubGroupPrefix = (model.productSubGroup == undefined || newVal == undefined) ? "" :
                            (newVal + " (" + model.productSubGroup + ")");
                    },
                },
                {
                    type: 'input',
                    inputType: 'text',
                    label: 'Product Sub Group',
                    model: 'product Sub Group',
                    required: true,
                    validator: ['string', 'required'],
                    onChanged: function(model, newVal, oldVal, field) {
                        model.productSubGroupPrefix = (newVal == undefined || model.productGroup == undefined) ? "" :
                            (model.productGroup + " (" + newVal + ")");
                    },
                },
                {
                    type: 'input',
                    inputType: 'text',
                    label: 'Product Group Prefix',
                    model: 'productSubGroupPrefix',
                    readonly: true,
                    required: true,
                    validator: ['string', 'required']
                },
                {
                    type: 'input',
                    inputType: 'number',
                    step: '0.01',
                    label: 'One side Hamali per Quintal',
                    model: 'onesideHamaliPerQuintal',
                    validator: ['number']
                },
                {
                    type: 'input',
                    inputType: 'number',
                    step: '0.01',
                    label: 'Yearly Rent per Quintal',
                    model: 'yearlyRentPerQuintal',
                    validator: ['number']
                }
            ]
        }
    ]
}
