const axios = require ( 'axios' );

export async function getAllProductGroups() {

    const response = await fetch('/api/referenceData/productGroups');
    return await response.json();
}

export async function createProductGroup(data) {
    const response = await fetch(`/api/referenceData/productGroup`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({productGroup: data})
    })
    return await response.json();
}

export async function deleteTrademark(productGroupId) {
    const response = await fetch(`/api/referenceData/trademark/${productGroupId}`, {method: 'DELETE'})
    return await response.json();
}

export async function editTrademark(data) {
    const response = await fetch(`/api/referenceData/productGroup`, {
        method: 'PUT',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({productGroup: data})
    })
    return await response.json();
}
