import { VueFormGenerator } from "vue-form-generator"

export default {
    groups: [
        {
            legend: "Lodgement Details",
            fields: [
                {
                    type: 'input',
                    inputType: 'text',
                    label: 'Lot Number',
                    model: 'lotNumber',
                    required: true,
                    validator: ['string', 'required']
                },
                {
                    type: 'vueMultiselect',
                    label: 'Trademark',
                    model: 'trademark',
                    required: true,
                    validator: ['string', 'required']
                },
                {
                    type: 'vueMultiselect',
                    label: 'Product',
                    model: 'product',
                    required: true,
                    validator: ['string', 'required']
                },
                {
                    type: 'input',
                    inputType: 'number',
                    label: 'Number of bags lodged',
                    model: 'numBagsLodged',
                    required: true,
                    validator: ['number', 'required']
                },
                {
                    type: 'switch',
                    inputType: 'text',
                    label: 'Platform Lot',
                    model: 'isPlatformLot',
                    required: true,
                    validator: ['string', 'required']
                },
                {
                    type: 'input',
                    inputType: 'text',
                    label: 'Locations in Store',
                    model: 'locationCodes',
                    required: true,
                    validator: ['string', 'required']
                },
            ]
        }
    ]
}
