import Vue from 'vue';
import VueRouter from 'vue-router';
import AppLayout from '../layout/index.vue';

Vue.use(VueRouter)

const routes = [
  {
    path: '/',
    component: AppLayout,
    children: [
      {
        path: '',
        name: 'AppDashboard',
        component: () => import('@/pages/dashboard')
      }
    ]
  },
  {
    path: '/inventory',
    component: AppLayout,
    children: [
      {
        path: 'lots',
        name: 'LotsList',
        component: () => import('@/pages/inventory/lists/LotsList.vue')
      },
      {
        path: 'lots/:_id',
        name: 'LotDetails',
        component: () => import('@/pages/inventory/lists/LotDetails.vue')
      },
      {
        path: 'lodgements',
        name: 'LodgementsList',
        component: () => import('@/pages/inventory/lists/LodgementsList.vue')
      },
      {
        path: 'lodgements/create',
        name: 'CreateLodgement',
        component: () => import('@/components/forms/CreateLodgement.vue')
      },
      {
        path: 'lodgements/createNew',
        name: 'CreateLodgementNew',
        component: () => import('@/components/forms/CreateLodgementNew.vue')
      },
      {
        path: 'deliveries',
        name: 'DeliveriesList',
        component: () => import('@/pages/inventory/lists/DeliveriesList.vue')
      }
    ]
  },
  {
    path: '/trademarks',
    component: AppLayout,
    children: [
      {
        path: '',
        name: 'TrademarksList',
        component: () => import('@/pages/trademarks')
      }
    ]
  },
  {
    path: '/products',
    component: AppLayout,
    children: [
      {
        path: '',
        name: 'ProductsList',
        component: () => import('@/pages/products')
      }
    ]
  },
  {
    path: '/referenceData',
    component: AppLayout,
    children: [
      {
        path: 'products',
        name: 'ProductsMasterList',
        component: () => import('@/pages/referenceData/ProductsMasterList.vue')
      },
      {
        path: 'productGroups',
        name: 'ProductGroupsMasterList',
        component: () => import('@/pages/referenceData/ProductGroupsMasterList.vue')
      },
      {
        path: 'trademarks',
        name: 'TrademarksMasterList',
        component: () => import('@/pages/referenceData/TrademarksMasterList.vue')
      },
      {
        path: 'employees',
        name: 'EmployeesMasterList',
        component: () => import('@/pages/referenceData/EmployeesMasterList.vue')
      }
    ]
  },
  // {
  //   path: '/:catchAll(.*)*',
  //   name: "PageNotFound",
  //   component: PageNotFound,
  //  },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

export default router
